function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),c=document.querySelector("body");document.querySelector("nav");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked"))}))}burgerMenu();let time=1800;const count=document.querySelector(".time");function updateCount(){const e=Math.floor(time/60);let t=time%60;t=t<10?"0"+t:t,count.innerHTML=`${e} : ${t}`,time--}setInterval(updateCount,1e3);